import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useSelector, useDispatch } from "react-redux";
import { increment, decrement } from "./actions";

function App() {
  const counter = useSelector(state => state.counter);
  const isLogged = useSelector(state => state.isLogged);
  const dispatch = useDispatch();
  var increment_by = 0;
  var decrement_by = 0;

  function getIncrementBy() {
    return increment_by;
  }

  function getDecrementBy() {
    return decrement_by;
  }

  function onIncrementChange(e) {
    console.log(e);
    console.log(e.target);
    console.log(e.target.value);
    var val = e.target.value;
    increment_by = val;
    document.querySelector("#increment_by").value = val;

  }

  function onDecrementChange(e) {
    var val = e.target.value;
    decrement_by = val;
    document.querySelector("#decrement_by").value = val;

  }

  function increase() {
    if (!isNaN(increment_by) && increment_by > 0) {
      dispatch(increment(increment_by));
    }
  }

  function decrease() {
    if (!isNaN(decrement_by) && decrement_by > 0) {
      dispatch(decrement(decrement_by));
    }
  }
  return (
    <div className="App">
      <h1>Counter:{counter} </h1>
      <button onClick={increase}>+</button>
      <input id="increment_by" type="number" value={getIncrementBy()} onChange={onIncrementChange} title="Increment by"></input>
      <button onClick={decrease}>-</button>
      <input id="decrement_by" type="number" value={getDecrementBy()} onChange={onDecrementChange} title="Increment by"></input>
      {isLogged ? <h3>Only be seen by the logged in users</h3> : <h5>Not Logged in</h5>}

    </div>
  );
}

export default App;
